import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import { Play, Pause, Volume2, VolumeX, Utensils, Search } from 'lucide-react';

// Video filter options - you can easily adjust these
const VIDEO_FILTER_OPTIONS = {
  current: 'light', // Current setting - lighter for better readability
  options: {
    light: {
      overlay: 'rgba(0, 0, 0, 0.3)', // 30% dark overlay (lighter)
      brightness: '110%',
      description: 'Light overlay - brighter video'
    },
    medium: {
      overlay: 'rgba(0, 0, 0, 0.5)', // 50% dark overlay (current)
      brightness: '100%', 
      description: 'Medium overlay - balanced'
    },
    dark: {
      overlay: 'rgba(0, 0, 0, 0.7)', // 70% dark overlay (darker)
      brightness: '90%',
      description: 'Dark overlay - dramatic effect'
    }
  }
};

const HeroContainer = styled.section<{ filterMode: string }>`
  position: relative;
  height: 100vh;
  min-height: 600px;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  background: #1a1a1a;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: ${props => VIDEO_FILTER_OPTIONS.options[props.filterMode as keyof typeof VIDEO_FILTER_OPTIONS.options].overlay};
    z-index: 2;
  }
`;

const VideoBackground = styled.video<{ filterMode: string }>`
  position: absolute;
  top: 50%;
  left: 50%;
  min-width: 100%;
  min-height: 100%;
  width: auto;
  height: auto;
  transform: translate(-50%, -50%);
  z-index: 1;
  object-fit: cover;
  filter: brightness(${props => VIDEO_FILTER_OPTIONS.options[props.filterMode as keyof typeof VIDEO_FILTER_OPTIONS.options].brightness});

  @media (max-width: 768px) {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const Content = styled.div`
  position: relative;
  z-index: 3;
  text-align: center;
  color: white;
  padding: 0 20px;
  max-width: 1200px;
  margin: 0 auto;
`;

const RestaurantIcon = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 20px;

  .burger-emoji {
    font-size: 3rem;
    margin-right: 10px;
  }

  .crown-emoji {
    font-size: 2rem;
  }
`;

const Title = styled.h1`
  font-family: 'Playfair Display', serif;
  font-size: clamp(3rem, 8vw, 6rem);
  font-weight: 800;
  margin-bottom: 20px;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.8);

  .royale {
    color: #FFD700;
    display: block;
  }

  .with-cheese {
    color: #DC2626;
    font-size: 0.8em;
    font-weight: 600;
  }

  @media (max-width: 768px) {
    font-size: clamp(2.5rem, 10vw, 4rem);
  }
`;

const Subtitle = styled.p`
  font-size: clamp(1rem, 3vw, 1.5rem);
  margin-bottom: 30px;
  font-style: italic;
  color: rgba(255, 255, 255, 0.9);
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.8);
  line-height: 1.4;

  @media (max-width: 768px) {
    font-size: clamp(0.9rem, 4vw, 1.2rem);
    padding: 0 10px;
  }
`;

const StatsContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 40px;
  margin-bottom: 40px;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 20px;
    align-items: center;
  }
`;

const StatItem = styled.div`
  text-align: center;
  background: rgba(255, 255, 255, 0.1);
  padding: 20px;
  border-radius: 15px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  min-width: 120px;

  @media (max-width: 768px) {
    min-width: 100px;
    padding: 15px;
  }
`;

const StatNumber = styled.div`
  font-size: 2.5rem;
  font-weight: 800;
  color: #FFD700;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.8);

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const StatLabel = styled.div`
  font-size: 0.9rem;
  color: rgba(255, 255, 255, 0.8);
  text-transform: uppercase;
  letter-spacing: 1px;
  margin-top: 5px;

  @media (max-width: 768px) {
    font-size: 0.8rem;
  }
`;

const CTAButton = styled.button`
  background: linear-gradient(135deg, #FFD700, #FFC300);
  color: #1a1a1a;
  border: none;
